import re
from collections import defaultdict

def to_key(value):
  if value is None:
    return None
  if isinstance(value, str):
    value = value.strip()
    return value or None
  return str(value)

def normalize_name(value):
  if not value:
    return ''
  # Strips leading emojis and spaces
  first_alnum_index = -1
  for i, char in enumerate(value):
      if char.isalnum():
          first_alnum_index = i
          break
  if first_alnum_index == -1:
      return ''
  value = value[first_alnum_index:]
  return ''.join(ch for ch in value.lower() if ch.isalnum())

def pick_value(*values):
  for value in values:
    if value is not None:
      return value
  return None

def build_payload(template, is_root):
  return {
    'templateId': template.get('id'),
    'parentTemplateId': pick_value(template.get('parent_id'), template.get('parentId')),
    'content': template.get('content', ''),
    'description': template.get('description') or '',
    'priority': template.get('priority'),
    'labels': template.get('labels') or [],
    'order': template.get('order'),
    'childOrder': pick_value(template.get('child_order'), template.get('childOrder')),
    'due': template.get('due') or {},
    'isRoot': is_root,
  }

def gather_templates():
  tasks = []
  for item in _('Fetch Template Tasks').all():
    payload = getattr(item, 'json', None) or {}
    if isinstance(payload, dict):
      tasks.append(payload)
  return tasks

def gather_projects():
  items = []
  for item in _('Fetch Projects').all():
    payload = getattr(item, 'json', None) or {}
    if isinstance(payload, dict):
      items.append(payload)
  return items

sel = _('Select Templates Project').first()
if not sel:
  return []
settings = getattr(sel, 'json', {}) or {}
templates_project_id = settings.get('templatesProjectId')
if not templates_project_id:
  return []

template_tasks = gather_templates()
if not template_tasks:
  return []

projects = gather_projects()

allowed_projects = set()
limit_to_test_project = True
test_project_name = 'üß™ Test (meta)'
if limit_to_test_project:
  for project in projects:
    name = (project.get('name') or '').strip()
    if name == test_project_name:
      pid = pick_value(project.get('id'), project.get('project_id'))
      if pid:
        allowed_projects.add(pid)
      break
else:
  for project in projects:
    pid = pick_value(project.get('id'), project.get('project_id'))
    if pid and pid != templates_project_id:
      allowed_projects.add(pid)

children = defaultdict(list)
for task in template_tasks:
  parent_raw = pick_value(task.get('parent_id'), task.get('parentId'))
  if parent_raw:
    parent_key = to_key(parent_raw)
    if parent_key:
      children[parent_key].append(task)

for siblings in children.values():
  siblings.sort(key=lambda x: pick_value(x.get('child_order'), x.get('childOrder')) or 0)

def walk_subtree(root):
  ordered = []
  stack = [root]
  while stack:
    node = stack.pop()
    ordered.append(node)
    node_key = to_key(pick_value(node.get('id'), node.get('task_id'), node.get('taskId')))
    kid_list = children.get(node_key, [])
    for child in reversed(kid_list):
      stack.append(child)
  return ordered

index = []
for task in template_tasks:
  name = normalize_name(task.get('content', ''))
  if name:
    index.append((task, name))

pattern = re.compile(r'^\$\s*(.+)', re.I)
results = []

def build_placeholder(payload):
  return {
    'id': payload.get('id'),
    'project_id': pick_value(payload.get('project_id'), payload.get('projectId')),
    'projectId': payload.get('projectId'),
    'section_id': pick_value(payload.get('section_id'), payload.get('sectionId')),
    'sectionId': payload.get('sectionId'),
    'parent_id': pick_value(payload.get('parent_id'), payload.get('parentId')),
    'parentId': payload.get('parentId'),
    'order': payload.get('order'),
    'child_order': pick_value(payload.get('child_order'), payload.get('childOrder')),
  }

for item in _input.all():
  payload = getattr(item, 'json', None) or {}
  if not isinstance(payload, dict):
    continue
  project_id = pick_value(payload.get('project_id'), payload.get('projectId'))
  if project_id == templates_project_id:
    continue
  if allowed_projects and project_id not in allowed_projects:
    continue
  match = pattern.match(payload.get('content') or '')
  if not match:
    continue
  template_name = normalize_name(match.group(1))
  if not template_name:
    continue
  candidates = [task for task, name in index if name == template_name]
  candidates.sort(key=lambda task: (pick_value(task.get('parent_id'), task.get('parentId')) is not None, pick_value(task.get('child_order'), task.get('childOrder')) or 0))
  root = candidates[0] if candidates else None
  if not root:
    continue
  if pick_value(root.get('parent_id'), root.get('parentId')):
    continue
  subtree = walk_subtree(root)
  if not subtree:
    continue

  placeholder = build_placeholder(payload)
  placeholder_key = to_key(placeholder.get('id'))
  if placeholder_key is None:
    continue

  total_creates = len(subtree)
  root_payload = build_payload(subtree[0], True)

  results.append({'json': {
    'action': 'preparePlaceholder',
    'placeholder': placeholder,
    'placeholderKey': placeholder_key,
    'rootTask': root_payload,
    'totalCreateActions': total_creates,
  }})

  for idx, tmpl in enumerate(subtree):
    task_payload = build_payload(tmpl, idx == 0)
    results.append({'json': {
      'action': 'createFromTemplate',
      'placeholder': placeholder,
      'placeholderKey': placeholder_key,
      'task': task_payload,
      'isLastAction': (idx + 1) == total_creates,
    }})

return results

