def extract_task_ids(payload):
  ids = []
  if isinstance(payload, dict):
    value = payload.get('id')
    if value is not None:
      ids.append(value)
    for key in ('data', 'task', 'json'):
      nested = payload.get(key)
      if nested is not None:
        ids.extend(extract_task_ids(nested))
    for nested in payload.values():
      if nested is payload:
        continue
      if isinstance(nested, (dict, list, tuple)):
        ids.extend(extract_task_ids(nested))
  elif isinstance(payload, (list, tuple)):
    for entry in payload:
      ids.extend(extract_task_ids(entry))
  return ids

data = _getWorkflowStaticData('global')
contexts = data.get('contexts') or {}
current_key = to_key(data.get('currentPlaceholderKey'))

out_items = []

for item in _input.all():
  payload = getattr(item, 'json', None)
  created_ids = extract_task_ids(payload)
  if not created_ids:
    raise ValueError('Failed to determine created task id')

  placeholder_key = current_key
  if placeholder_key is None and len(contexts) == 1:
    placeholder_key = next(iter(contexts.keys()))
  if placeholder_key is None:
    raise ValueError('Missing placeholder key when updating parent map')

  context = contexts.get(placeholder_key)
  if context is None:
    raise ValueError(f"Missing template context for placeholder {placeholder_key}")

  pending = context.get('pendingTemplates')
  if not isinstance(pending, list):
    pending = []

  parent_map = context.get('createdByTemplate')
  if not isinstance(parent_map, dict):
    parent_map = {}

  for value in created_ids:
    if not pending:
      raise ValueError('Pending template queue is empty')
    template_id = to_key(pending.pop(0))
    if template_id is None:
      raise ValueError('Failed to normalize template id for created task')
    created_id = to_identifier(value)
    if created_id is None:
      raise ValueError('Failed to normalize created task id')
    parent_map[template_id] = created_id
    remaining = context.get('remainingCreates')
    if isinstance(remaining, int):
      context['remainingCreates'] = max(0, remaining - 1)

  context['createdByTemplate'] = parent_map
  context['pendingTemplates'] = pending

  done = not pending and context.get('remainingCreates') in (None, 0)
  if done:
    contexts.pop(placeholder_key, None)
    if to_key(data.get('currentPlaceholderKey')) == placeholder_key:
      data['currentPlaceholderKey'] = None
    current_key = None
  else:
    contexts[placeholder_key] = context
    data['currentPlaceholderKey'] = placeholder_key
    current_key = placeholder_key

  out_items.append({'json': payload or {}})

if contexts:
