import re
from collections import defaultdict, deque

DEBUG = False
LIMIT_TO_TEST_PROJECT = True
TEST_PROJECT_NAME = "üß™ Test (meta)"


def normalize_name(value):
    if not value:
        return ""
    lowered = value.lower()
    return "".join(ch for ch in lowered if ch.isalnum())


template_project_item = _('Select Templates Project').first()
if not template_project_item:
    if DEBUG:
        print('Templates project not found')
    return []

template_project_id = template_project_item.json.get('templatesProjectId')
if template_project_id is None:
    if DEBUG:
        print('Templates project id missing')
    return []

template_tasks = [item.json for item in _('Fetch Template Tasks').all()]
if not template_tasks:
    if DEBUG:
        print('Templates project has no tasks')
    return []

projects = [item.json for item in _('Fetch Projects').all()]
allowed_project_ids = set()
if LIMIT_TO_TEST_PROJECT:
    for project in projects:
        name = (project.get('name') or '').strip()
        if name == TEST_PROJECT_NAME:
            allowed_project_ids.add(project.get('id'))
            break
    if not allowed_project_ids:
        if DEBUG:
            print(f"Project {TEST_PROJECT_NAME!r} not found")
        return []
else:
    for project in projects:
        project_id = project.get('id')
        if project_id and project_id != template_project_id:
            allowed_project_ids.add(project_id)

children = defaultdict(list)
for task in template_tasks:
    parent_id = task.get('parent_id')
    if parent_id:
        children[parent_id].append(task)

for siblings in children.values():
    siblings.sort(key=lambda item: item.get('child_order', 0))

template_index = [
    (task, normalize_name(task.get('content', '')))
    for task in template_tasks
]

placeholder_pattern = re.compile(r'^!!\s*(.+)$', re.IGNORECASE)
items = []
all_tasks = [item.json for item in _input.all()]

for task in all_tasks:
    project_id = task.get('project_id')
    if project_id == template_project_id:
        continue
    if allowed_project_ids and project_id not in allowed_project_ids:
        continue

    match = placeholder_pattern.match(task.get('content', ''))
    if not match:
        continue

    template_name = match.group(1).strip()
    normalized_placeholder = normalize_name(template_name)
    if not normalized_placeholder:
        continue

    candidates = [
        template_task
        for template_task, normalized in template_index
        if normalized == normalized_placeholder
    ]
    candidates.sort(
        key=lambda template_task: (
            template_task.get('parent_id') is not None,
            template_task.get('child_order', 0),
        )
    )
    if not candidates:
        if DEBUG:
            print(f"No template found for placeholder {template_name!r}")
        continue

    root = candidates[0]
    queue = deque([root])
    subtree = []
    while queue:
        current = queue.popleft()
        subtree.append(current)
        for child in children.get(current.get('id'), []):
            queue.append(child)

    if not subtree:
        continue

    placeholder = {
        'id': task.get('id'),
        'projectId': project_id,
        'sectionId': task.get('section_id'),
        'parentId': task.get('parent_id'),
        'order': task.get('order'),
        'childOrder': task.get('child_order'),
    }
    placeholder_key = str(task.get('id'))

    total_actions = len(subtree)
    items.append(
        {
            'json': {
                'action': 'deletePlaceholder',
                'placeholder': placeholder,
                'templateName': template_name,
                'placeholderKey': placeholder_key,
                'totalCreateActions': total_actions,
            }
        }
    )

    for index, template_task in enumerate(subtree):
        template_id = template_task.get('id')
        payload = {
            'templateId': template_id,
            'parentTemplateId': template_task.get('parent_id'),
            'content': template_task.get('content', ''),
            'description': template_task.get('description') or '',
            'priority': template_task.get('priority'),
            'labels': template_task.get('labels') or [],
            'order': template_task.get('order'),
            'childOrder': template_task.get('child_order'),
            'due': template_task.get('due') or {},
            'isRoot': template_id == root.get('id'),
        }

        items.append(
            {
                'json': {
                    'action': 'createFromTemplate',
                    'placeholder': placeholder,
                    'templateName': template_name,
                    'placeholderKey': placeholder_key,
                    'task': payload,
                    'isLastAction': index == total_actions - 1,
                }
            }
        )

return items

def get_item_json(item):
    if hasattr(item, 'json'):
        return item.json
    if isinstance(item, dict):
        return item.get('json', {})
    return {}


def to_output_item(item):
    item_json = get_item_json(item)
    result = {'json': item_json}
    binary = getattr(item, 'binary', None)
    if binary is None and isinstance(item, dict):
        binary = item.get('binary')
    if binary is not None:
        result['binary'] = binary
    paired = getattr(item, 'pairedItem', None)
    if paired is None and isinstance(item, dict):
        paired = item.get('pairedItem')
    if paired is not None:
        result['pairedItem'] = paired
    return result


