data = _self.getWorkflowStaticData('node')
items = _input.all()
if not items:
    raise ValueError('Missing input item for task creation')

item_json = get_item_json(items[0])
placeholder_key = item_json.get('placeholderKey')
if not placeholder_key:
    raise ValueError('Missing placeholder key for task creation')

contexts = data.get('contexts') or {}
context = contexts.get(placeholder_key)
if not context:
    raise ValueError(f"No placeholder context for key {placeholder_key}")

placeholder = context.get('placeholder')
if not placeholder:
    raise ValueError('Placeholder context is missing details')

parent_map = context.get('parentMap') or {}
task = item_json.get('task') or {}
if not task:
    raise ValueError('Missing template task payload')

options = {}
section_id = placeholder.get('sectionId')

description = task.get('description')
if description:
    options['description'] = description

priority = task.get('priority')
if isinstance(priority, int) and priority > 0:
    options['priority'] = priority

if task.get('isRoot'):
    if section_id is not None:
        options['section'] = section_id
    parent_id = placeholder.get('parentId')
    if parent_id is not None:
        options['parentId'] = parent_id
else:
    parent_template_id = task.get('parentTemplateId')
    if parent_template_id is not None:
        parent_id = parent_map.get(parent_template_id)
        if parent_id is None:
            raise ValueError(
                f"Parent task for template {parent_template_id} has not been created yet"
            )
        options['parentId'] = parent_id

due = task.get('due') or {}
if due.get('string'):
    options['dueString'] = due.get('string')
if due.get('datetime'):
    options['dueDateTime'] = due.get('datetime')
elif due.get('date'):
    options['dueDate'] = due.get('date')
if due.get('lang'):
    options['dueLang'] = due.get('lang')

labels = task.get('labels') or []
if not isinstance(labels, list):
    labels = [labels]

create_payload = {
    'content': task.get('content', ''),
    'project': placeholder.get('projectId'),
    'labels': labels,
    'options': options,
    'templateId': task.get('templateId'),
    'placeholderKey': placeholder_key,
    'isLastAction': item_json.get('isLastAction', False),
}

context['parentMap'] = parent_map
contexts[placeholder_key] = context
data['contexts'] = contexts

output_json = {
    'placeholder': placeholder,
    'placeholderKey': placeholder_key,
    'task': task,
    'create': create_payload,
    'isLastAction': item_json.get('isLastAction', False),
}

return [to_output_item(output_json)]

