items = _input.all()
if not items:
    return []

item_json = get_item_json(items[0])
response = item_json.get('data') or item_json
new_task_id = None
if isinstance(response, dict):
    new_task_id = response.get('id') or response.get('task', {}).get('id')

if new_task_id is None:
    raise ValueError('Failed to determine created task id')

create_payload = item_json.get('create') or {}
placeholder_key = create_payload.get('placeholderKey') or item_json.get('placeholderKey')
if not placeholder_key:
    raise ValueError('Missing placeholder key when updating parent map')

contexts = data.get('contexts') or {}
context = contexts.get(placeholder_key)
if context is None:
    context = {
        'placeholder': item_json.get('placeholder'),
        'parentMap': {},
        'remainingCreates': 0,
    }

parent_map = context.get('parentMap') or {}
template_id = create_payload.get('templateId')
if template_id is None:
    template_id = item_json.get('task', {}).get('templateId')
if template_id is None:
    raise ValueError('Missing template id for created task')

parent_map[template_id] = new_task_id
context['parentMap'] = parent_map

remaining = context.get('remainingCreates')
if isinstance(remaining, int) and remaining > 0:
    context['remainingCreates'] = remaining - 1

