# Update Parent Map â€” JsProxy-safe, handles dict/list/sequence payloads and batches

def is_string_like(x):
  return isinstance(x, (str, bytes, bytearray))

def is_mapping_like(x):
  # Works for dict and JsProxy objects that behave like a mapping
  if isinstance(x, dict):
    return True
  return hasattr(x, "keys") or hasattr(x, "items")

def is_sequence_like(x):
  # Works for list/tuple and JsProxy arrays
  if isinstance(x, (list, tuple)):
    return True
  if is_string_like(x):
    return False
  return hasattr(x, "__len__") and hasattr(x, "__getitem__")

def mget(obj, key, default=None):
  """Mapping get for dict/JsProxy mapping."""
  if isinstance(obj, dict):
    return obj.get(key, default)
  # JsProxy mapping: try key lookup
  try:
    val = obj[key]
    return val
  except Exception:
    pass
  # Try attribute (last resort)
  try:
    return getattr(obj, key)
  except Exception:
    return default

def iter_values(obj):
  """Yield values of a mapping-like object."""
  if isinstance(obj, dict):
    for v in obj.values():
      yield v
    return
  # JsProxy: try .values(), then items()
  try:
    vals = obj.values()  # may exist on JsProxy
    for v in vals:
      yield v
    return
  except Exception:
    pass
  try:
    for _, v in obj.items():
      yield v
    return
  except Exception:
    pass
  # Fallback: try keys()
  try:
    for k in obj.keys():
      yield mget(obj, k)
  except Exception:
    return

def iter_task_ids(payload):
  """
  Yield Todoist task IDs from any payload:
  - dict/JsProxy mapping with 'id'
  - list/tuple/JsProxy array of such dicts
  - nested under 'data' / 'task' / 'json'
  """
  # Unwrap n8n items
  if hasattr(payload, "json"):
    payload = payload.json

  # Mapping-like
  if is_mapping_like(payload):
    tid = mget(payload, "id")
    if tid:
      yield tid
      return
    # common wrappers
    for k in ("data", "task", "json"):
      inner = mget(payload, k)
      if inner is not None:
        for x in iter_task_ids(inner):
          yield x
        # do not return; allow other wrappers too
    # fallback: walk values
    for v in iter_values(payload):
      for x in iter_task_ids(v):
        yield x
    return

  # Sequence-like (incl. JsProxy arrays)
  if is_sequence_like(payload):
    try:
      n = len(payload)
    except Exception:
      n = 0
    for i in range(n):
      try:
        v = payload[i]
      except Exception:
        continue
      for x in iter_task_ids(v):
        yield x
    return

  # nothing found
  return

def to_str(v):
  return None if v is None else str(v)

def get_static():
  return _getWorkflowStaticData("node")  # type: ignore[name-defined]

data = get_static()
contexts = data.get("contexts") or {}
current_key = to_str(data.get("currentPlaceholderKey"))

out_items = []

# Process all create results (there can be multiple)
for item in _input.all():
  # Extract ALL ids from the payload (handles list/dict/JsProxy)
  created_ids = list(iter_task_ids(item))
  if not created_ids:
    # Try the raw json as a fallback (in case the input wrapper differs)
    created_ids = list(iter_task_ids(getattr(item, "json", None)))
  if not created_ids:
    raise ValueError("Failed to determine created task id")

  # Decide which placeholder context to use
  placeholder_key = current_key
  if placeholder_key is None:
    j = getattr(item, "json", None)
    if is_mapping_like(j):
      placeholder_key = to_str(mget(j, "placeholderKey"))
  if placeholder_key is None:
    raise ValueError("Missing placeholder key when updating parent map")

  ctx = contexts.get(placeholder_key)
  if ctx is None:
    # Defensive bootstrap (usually exists already)
    ctx = {
      "placeholder": {},
      "parentMap": {},
      "remainingCreates": 0,
      "pendingTemplateIds": [],
    }

  parent_map = ctx.get("parentMap") or {}
  pending = list(ctx.get("pendingTemplateIds") or [])

  # Map each new id to the next pending templateId (FIFO)
  for new_id in created_ids:
    if pending:
      templ_id = pending.pop(0)
    else:
      # Rare fallback: try echo
      templ_id = None
      j = getattr(item, "json", None)
      if is_mapping_like(j):
        task_payload = mget(j, "task", {})
        if is_mapping_like(task_payload):
          templ_id = mget(task_payload, "templateId")
    if templ_id is None:
      raise ValueError("Missing template id for created task (pending queue empty)")
    parent_map[templ_id] = new_id

  # Save updates
  ctx["parentMap"] = parent_map
  ctx["pendingTemplateIds"] = pending

  # Decrement remainingCreates by the number we just mapped (usually 1)
  rem = ctx.get("remainingCreates")
  if isinstance(rem, int) and rem > 0:
    ctx["remainingCreates"] = max(0, rem - len(created_ids))

  # Decide if we've finished this placeholder
  is_last = ctx.get("remainingCreates") in (None, 0)
  j = getattr(item, "json", None)
  if is_mapping_like(j) and mget(j, "isLastAction"):
    is_last = True

  if is_last:
    contexts.pop(placeholder_key, None)
    if to_str(data.get("currentPlaceholderKey")) == placeholder_key:
      data["currentPlaceholderKey"] = None
    # Let the next placeholder set its key
    current_key = None
  else:
    contexts[placeholder_key] = ctx

  # Pass-through for downstream logging if needed
  out_items.append({"json": getattr(item, "json", {}) or {}})

# Persist back
data["contexts"] = contexts if contexts else {}
if not contexts:
  data["currentPlaceholderKey"] = None

return out_items
