import re
from collections import defaultdict

def normalize_name(value):
  if not value:
    return ''
  return ''.join(ch for ch in value.lower() if ch.isalnum())

sel = _('Select Templates Project').first()
if not sel:
  return []
TEMPLATES_PROJ_ID = sel.json.get('templatesProjectId')
if not TEMPLATES_PROJ_ID:
  return []

TEMPLATE_TASKS = [i.json for i in _('Fetch Template Tasks').all()]
if not TEMPLATE_TASKS:
  return []

projects = [i.json for i in _('Fetch Projects').all()]
allowed = set()
LIMIT_TO_TEST_PROJECT = True
TEST_PROJECT_NAME = 'üß™ Test (meta)'
if LIMIT_TO_TEST_PROJECT:
  for p in projects:
    if (p.get('name') or '').strip() == TEST_PROJECT_NAME:
      allowed.add(p.get('id'))
      break
else:
  for p in projects:
    pid = p.get('id')
    if pid and pid != TEMPLATES_PROJ_ID:
      allowed.add(pid)

children = defaultdict(list)
for t in TEMPLATE_TASKS:
  pid = t.get('parent_id')
  if pid:
    children[pid].append(t)
for sibs in children.values():
  sibs.sort(key=lambda x: x.get('child_order', 0))

def collect_subtree(root):
  ordered = []
  stack = [root]
  while stack:
    node = stack.pop()
    ordered.append(node)
    kids = children.get(node.get('id'), [])
    for child in reversed(kids):
      stack.append(child)
  return ordered

index = [(t, normalize_name(t.get('content',''))) for t in TEMPLATE_TASKS]
pat = re.compile(r'^!!\s*(.+)$', re.I)
items = []

for doc in _input.all():
  task = doc.json
  proj_id = task.get('project_id')
  if proj_id == TEMPLATES_PROJ_ID:
    continue
  if allowed and proj_id not in allowed:
    continue
  m = pat.match(task.get('content','') or '')
  if not m:
    continue
  template_name = m.group(1).strip()
  key = normalize_name(template_name)
  if not key:
    continue
  cands = [tt for tt,n in index if n == key]
  cands.sort(key=lambda tt: (tt.get('parent_id') is not None, tt.get('child_order', 0)))
  if not cands:
    continue

  root = cands[0]
  subtree = collect_subtree(root)
  if not subtree:
    continue

  placeholder = {
    'id': task.get('id'),
    'project_id': proj_id,
    'section_id': task.get('section_id'),
    'parent_id': task.get('parent_id'),
    'order': task.get('order'),
    'child_order': task.get('child_order')
  }
  ph_key = str(task.get('id'))

  items.append({ 'json': {
    'action': 'deletePlaceholder',
    'placeholder': placeholder,
    'placeholderKey': ph_key,
    'totalCreateActions': len(subtree)
  }})

  for i, tmpl in enumerate(subtree):
    payload = {
      'templateId': tmpl.get('id'),
      'parentTemplateId': tmpl.get('parent_id'),
      'content': tmpl.get('content',''),
      'description': tmpl.get('description') or '',
      'priority': tmpl.get('priority'),
      'labels': tmpl.get('labels') or [],
      'order': tmpl.get('order'),
      'childOrder': tmpl.get('child_order'),
      'due': tmpl.get('due') or {},
      'isRoot': tmpl.get('id') == root.get('id')
    }
    items.append({ 'json': {
      'action': 'createFromTemplate',
      'placeholder': placeholder,
      'placeholderKey': ph_key,
      'task': payload,
      'isLastAction': i == len(subtree) - 1
    }})

return items

def get_json(item):
  if hasattr(item, 'json'):
    return item.json
  if isinstance(item, dict):
    return item.get('json', {})
  return {}

def to_key(value):
  if value is None:
    return None
  if isinstance(value, str):
    value = value.strip()
    return value or None
  return str(value)

def normalize_parent_map(raw):
  if isinstance(raw, dict):
    cleaned = {}
    for key, value in raw.items():
      norm_key = to_key(key)
      if norm_key is None:
        continue
      ident = to_identifier(value)
      if ident is None:
        continue
      cleaned[norm_key] = ident
    return cleaned
  return {}

def cleaned_option(value):
  if value is None:
    return None
  if isinstance(value, (list, dict)) and not value:
    return None
  if isinstance(value, str):
    stripped = value.strip()
    if not stripped:
      return None
    return stripped
  return value

def to_identifier(value):
  if value is None:
    return None
  if isinstance(value, str):
    value = value.strip()
    if not value:
      return None
    return value
  return value

data = _getWorkflowStaticData('global')
items = _input.all()
if not items:
  return []
item_json = get_json(items[0])
ph = item_json.get('placeholder') or {}
raw_placeholder_key = item_json.get('placeholderKey')
if raw_placeholder_key is None:
  raw_placeholder_key = ph.get('id')
if raw_placeholder_key is None:
  raise ValueError('Missing placeholder key for task creation')
placeholder_key = str(raw_placeholder_key)
data['currentPlaceholderKey'] = placeholder_key

contexts = data.get('contexts') or {}
context = contexts.get(placeholder_key)
if not context:
  context = {
    'placeholder': ph,
    'parentMap': {},
    'remainingCreates': item_json.get('totalCreateActions', 0),
    'pendingTemplateIds': [],
  }
  contexts[placeholder_key] = context
  data['contexts'] = contexts

parent_map = normalize_parent_map(context.get('parentMap'))
context['parentMap'] = parent_map

task = item_json.get('task') or {}
if not task:
  raise ValueError('Missing template task payload')

options = {}
if task.get('isRoot'):
  section_id = to_identifier(ph.get('section_id'))
  if section_id is not None:
    options['sectionId'] = section_id
  parent_from_placeholder = to_identifier(ph.get('parent_id'))
  if parent_from_placeholder is not None:
    options['parentId'] = parent_from_placeholder
else:
  parent_template_id = task.get('parentTemplateId')
  if parent_template_id is not None:
    parent_template_key = str(parent_template_id)
    parent_id = to_identifier(parent_map.get(parent_template_key))
    if parent_id is None:
      raise ValueError(f"Parent for template {parent_template_key} not created yet - batching should prevent this")
    options['parentId'] = parent_id

description = cleaned_option(task.get('description'))
if description is not None:
  options['description'] = description
prio = task.get('priority')
if isinstance(prio, int) and prio > 0:
  options['priority'] = prio

due = task.get('due') or {}
due_string = cleaned_option(due.get('string'))
if due_string is not None:
  options['dueString'] = due_string
else:
  due_datetime = cleaned_option(due.get('datetime'))
  if due_datetime is not None:
    options['dueDateTime'] = due_datetime
  else:
    due_date = cleaned_option(due.get('date'))
    if due_date is not None:
      options['dueDate'] = due_date

labels = task.get('labels') or []
if not isinstance(labels, list):
  labels = [labels]
normalized_labels = []
for label in labels:
  ident = to_identifier(label)
  if ident is not None:
    normalized_labels.append(ident)

template_id = task.get('templateId')
if template_id is None:
  raise ValueError('Missing template id in template task payload')
template_id = str(template_id)

existing_pending = context.get('pendingTemplateIds') or []
if isinstance(existing_pending, list):
  pending = [str(x) for x in existing_pending if x is not None]
else:
  pending = []
pending.append(template_id)
context['pendingTemplateIds'] = pending
contexts[placeholder_key] = context
data['contexts'] = contexts

create_options = {k: v for k, v in options.items() if cleaned_option(v) is not None}

create_payload = {
  'content': task.get('content', ''),
  'project': to_identifier(ph.get('project_id')),
  'labels': normalized_labels,
  'options': create_options,
  'templateId': template_id,
  'placeholderKey': placeholder_key,
  'isLastAction': item_json.get('isLastAction', False),
}

return [{'json': {**item_json, 'create': create_payload}}]

def is_string_like(x):
  return isinstance(x, (str, bytes, bytearray))

def is_mapping_like(x):
  if isinstance(x, dict):
    return True
  return hasattr(x, 'keys') or hasattr(x, 'items')

def is_sequence_like(x):
  if isinstance(x, (list, tuple)):
    return True
  if is_string_like(x):
    return False
  return hasattr(x, '__len__') and hasattr(x, '__getitem__')

def mget(obj, key, default=None):
  if isinstance(obj, dict):
    return obj.get(key, default)
  try:
    return obj[key]
  except Exception:
    pass
  try:
    return getattr(obj, key)
  except Exception:
    return default

def iter_values(obj):
  if isinstance(obj, dict):
    for v in obj.values():
      yield v
    return
  try:
    for v in obj.values():
      yield v
    return
  except Exception:
    pass
  try:
    for _, v in obj.items():
      yield v
    return
  except Exception:
    pass
  try:
    for k in obj.keys():
      yield mget(obj, k)
  except Exception:
    return

def iter_task_ids(payload):
  if hasattr(payload, 'json'):
    payload = payload.json
  if is_mapping_like(payload):
    tid = mget(payload, 'id')
    if tid is not None:
      yield tid
    for k in ('data', 'task', 'json'):
      inner = mget(payload, k)
      if inner is not None:
        for x in iter_task_ids(inner):
          yield x
    for v in iter_values(payload):
      for x in iter_task_ids(v):
        yield x
    return
  if is_sequence_like(payload):
    try:
      n = len(payload)
    except Exception:
      n = 0
    for i in range(n):
      try:
        v = payload[i]
      except Exception:
        continue
      for x in iter_task_ids(v):
        yield x
    return
  return

def to_key(value):
  if value is None:
    return None
  if isinstance(value, str):
    value = value.strip()
    return value or None
  return str(value)

def to_identifier(value):
  if value is None:
    return None
  if isinstance(value, bool):
    value = int(value)
  if isinstance(value, (int, float)):
    if isinstance(value, float) and not value.is_integer():
      return str(value)
    return int(value)
  if isinstance(value, str):
    value = value.strip()
    return value or None
  return to_key(value)

data = _getWorkflowStaticData('global')
contexts = data.get('contexts') or {}
current_key = to_key(data.get('currentPlaceholderKey'))

out_items = []

for item in _input.all():
  created_ids = [x for x in iter_task_ids(item) if x is not None]
  if not created_ids:
    j = getattr(item, 'json', None)
    created_ids = [x for x in iter_task_ids(j) if x is not None]
  if not created_ids:
    raise ValueError('Failed to determine created task id')

  placeholder_key = current_key
  if placeholder_key is None and isinstance(contexts, dict) and len(contexts) == 1:
    placeholder_key = to_key(next(iter(contexts.keys()), None))
  if placeholder_key is None:
    j = getattr(item, 'json', None)
    if is_mapping_like(j):
      placeholder_key = to_key(mget(j, 'placeholderKey'))
  if placeholder_key is None:
    raise ValueError('Missing placeholder key when updating parent map')

  ctx = contexts.get(placeholder_key)
  if ctx is None:
    ctx = {
      'placeholder': {},
      'parentMap': {},
      'remainingCreates': 0,
      'pendingTemplateIds': [],
    }

  parent_map = ctx.get('parentMap')
  if not isinstance(parent_map, dict):
    parent_map = {}
  else:
    cleaned = {}
    for k, v in parent_map.items():
      norm_key = to_key(k)
      if norm_key is None:
        continue
      norm_value = to_identifier(v)
      if norm_value is None:
        continue
      cleaned[norm_key] = norm_value
    parent_map = cleaned

  pending = [to_key(x) for x in (ctx.get('pendingTemplateIds') or []) if to_key(x) is not None]

  for new_id in created_ids:
    templ_id = None
    if pending:
      templ_id = pending.pop(0)
    if templ_id is None:
      j = getattr(item, 'json', None)
      if is_mapping_like(j):
        tp = mget(j, 'task', {})
        if is_mapping_like(tp):
          templ_id = to_key(mget(tp, 'templateId'))
    if templ_id is None:
      raise ValueError('Missing template id for created task (pending queue empty)')
    stored_id = to_identifier(new_id)
    if stored_id is None:
      raise ValueError('Failed to normalize created task id')
    parent_map[to_key(templ_id)] = stored_id

  ctx['parentMap'] = parent_map
  ctx['pendingTemplateIds'] = pending

  rem = ctx.get('remainingCreates')
  if isinstance(rem, int) and rem > 0:
    ctx['remainingCreates'] = max(0, rem - len(created_ids))

  is_last = ctx.get('remainingCreates') in (None, 0)
  j = getattr(item, 'json', None)
  if is_mapping_like(j) and mget(j, 'isLastAction'):
    is_last = True

  if is_last:
    contexts.pop(placeholder_key, None)
    if to_key(data.get('currentPlaceholderKey')) == placeholder_key:
      data['currentPlaceholderKey'] = None
    current_key = None
  else:
    contexts[placeholder_key] = ctx
    current_key = placeholder_key

  out_items.append({'json': getattr(item, 'json', {}) or {}})

data['contexts'] = contexts if contexts else {}
if not contexts:
  data['currentPlaceholderKey'] = None

return out_items
