def _id(o):
  if isinstance(o,dict):
    if o.get('id'):return o['id']
    for k in('task','data'):
      if k in o:
        r=_id(o[k]);
        if r:return r
    for v in o.values():
      r=_id(v)
      if r:return r
  elif isinstance(o,list):
    for v in o:
      r=_id(v)
      if r:return r
  return None

