import re
from collections import defaultdict, deque

DEBUG = False
LIMIT_TO_TEST_PROJECT = True
TEST_PROJECT_NAME = 'ðŸ§ª Test (meta)'

def normalize_name(value):
  if not value:
    return ''
  lowered = value.lower()
  return ''.join(ch for ch in lowered if ch.isalnum())

sel = _('Select Templates Project').first()
if not sel:
  return []

templates_project_id = sel.json.get('templatesProjectId')
if not templates_project_id:
  return []

template_tasks = [it.json for it in _('Fetch Template Tasks').all()]
if not template_tasks:
  return []

projects = [it.json for it in _('Fetch Projects').all()]
allowed_project_ids = set()
if LIMIT_TO_TEST_PROJECT:
  for p in projects:
    if (p.get('name') or '').strip() == TEST_PROJECT_NAME:
      allowed_project_ids.add(p.get('id'))
      break
else:
  for p in projects:
    pid = p.get('id')
    if pid and pid != templates_project_id:
      allowed_project_ids.add(pid)

children = defaultdict(list)
for t in template_tasks:
  pid = t.get('parent_id')
  if pid:
    children[pid].append(t)
for sibs in children.values():
  sibs.sort(key=lambda x: x.get('child_order', 0))

index = [(t, normalize_name(t.get('content', ''))) for t in template_tasks]
pat = re.compile(r'^!!\s*(.+)$', re.I)
items = []

for t in [it.json for it in _input.all()]:
  pid = t.get('project_id')
  if pid == templates_project_id:
    continue
  if allowed_project_ids and pid not in allowed_project_ids:
    continue
  m = pat.match(t.get('content', '') or '')
  if not m:
    continue
  name = m.group(1).strip()
  key = normalize_name(name)
  if not key:
    continue
  candidates = [tt for tt, n in index if n == key]
  candidates.sort(key=lambda tt: (tt.get('parent_id') is not None, tt.get('child_order', 0)))
  if not candidates:
    continue
  root = candidates[0]
  q = deque([root])
  subtree = []
  while q:
    cur = q.popleft()
    subtree.append(cur)
    for ch in children.get(cur.get('id'), []):
      q.append(ch)
  if not subtree:
    continue
  ph = {
    'id': t.get('id'),
    'projectId': pid,
    'sectionId': t.get('section_id'),
    'parentId': t.get('parent_id'),
    'order': t.get('order'),
    'childOrder': t.get('child_order')
  }
  phk = str(t.get('id'))
  items.append({ 'json': {
    'action': 'deletePlaceholder',
    'placeholder': ph,
    'templateName': name,
    'placeholderKey': phk,
    'totalCreateActions': len(subtree)
  }})
  for i, tt in enumerate(subtree):
    tid = tt.get('id')
    payload = {
      'templateId': tid,
      'parentTemplateId': tt.get('parent_id'),
      'content': tt.get('content', ''),
      'description': tt.get('description') or '',
      'priority': tt.get('priority'),
      'labels': tt.get('labels') or [],
      'order': tt.get('order'),
      'childOrder': tt.get('child_order'),
      'due': tt.get('due') or {},
      'isRoot': tid == root.get('id')
    }
    items.append({ 'json': {
      'action': 'createFromTemplate',
      'placeholder': ph,
      'templateName': name,
      'placeholderKey': phk,
      'task': payload,
      'isLastAction': i == len(subtree) - 1
    }})

return items

