def get_item_json(item):
  if hasattr(item, 'json'):
    return item.json
  if isinstance(item, dict):
    return item.get('json', {})
  return {}

def get_static_data():
  return _getWorkflowStaticData('node')

data = get_static_data()
items = _input.all()
if not items:
  raise ValueError('Missing input item for task creation')

item_json = get_item_json(items[0])
placeholder_key = item_json.get('placeholderKey')
if placeholder_key is None:
  raise ValueError('Missing placeholder key for task creation')
placeholder_key = str(placeholder_key)

# ensure current key is set so Update Parent Map can find it even if branches race
data['currentPlaceholderKey'] = placeholder_key

contexts = data.get('contexts') or {}
context = contexts.get(placeholder_key)
if not context:
  context = {
    'placeholder': item_json.get('placeholder') or {},
    'parentMap': {},
    'remainingCreates': item_json.get('totalCreateActions', 0),
    'pendingTemplateIds': []
  }
  contexts[placeholder_key] = context
  data['contexts'] = contexts

placeholder = context.get('placeholder')
if not placeholder:
  raise ValueError('Placeholder context is missing details')

parent_map = context.get('parentMap') or {}
pending = context.get('pendingTemplateIds') or []

task = item_json.get('task') or {}
if not task:
  raise ValueError('Missing template task payload')

options = {}
section_id = placeholder.get('sectionId')

desc = task.get('description')
if desc:
  options['description'] = desc

priority = task.get('priority')
if isinstance(priority, int) and priority > 0:
  options['priority'] = priority

if task.get('isRoot'):
  if section_id is not None:
    options['section'] = section_id
  parent_id = placeholder.get('parentId')
  if parent_id is not None:
    options['parentId'] = parent_id
else:
  parent_template_id = task.get('parentTemplateId')
  if parent_template_id is not None:
    parent_id = parent_map.get(parent_template_id)
    if parent_id is None:
      raise ValueError(f"Parent task for template {parent_template_id} has not been created yet")
    options['parentId'] = parent_id

due = task.get('due') or {}
if due.get('string'):
  options['dueString'] = due.get('string')
if due.get('datetime'):
  options['dueDateTime'] = due.get('datetime')
elif due.get('date'):
  options['dueDate'] = due.get('date')
if due.get('lang'):
  options['dueLang'] = due.get('lang')

labels = task.get('labels') or []
if not isinstance(labels, list):
  labels = [labels]

# enqueue template id so mapper can match by order
template_id = task.get('templateId')
if template_id is None:
  raise ValueError('Missing template id in template task payload')

pending.append(template_id)
context['pendingTemplateIds'] = pending

create_payload = {
  'content': task.get('content', ''),
  'project': placeholder.get('projectId'),
  'labels': labels,
  'options': options,
  'templateId': template_id,
  'placeholderKey': placeholder_key,
  'isLastAction': item_json.get('isLastAction', False)
}

contexts[placeholder_key] = context
data['contexts'] = contexts

return [{ 'json': {
  'placeholder': placeholder,
  'placeholderKey': placeholder_key,
  'task': task,
  'create': create_payload,
  'isLastAction': item_json.get('isLastAction', False)
}}]

def is_string_like(x):
  return isinstance(x, (str, bytes, bytearray))

def is_mapping_like(x):
  if isinstance(x, dict):
    return True
  return hasattr(x, 'keys') or hasattr(x, 'items')

def is_sequence_like(x):
  if isinstance(x, (list, tuple)):
    return True
  if is_string_like(x):
    return False
  return hasattr(x, '__len__') and hasattr(x, '__getitem__')

def mget(obj, key, default=None):
  if isinstance(obj, dict):
    return obj.get(key, default)
  try:
    return obj[key]
  except Exception:
    pass
  try:
    return getattr(obj, key)
  except Exception:
    return default

def iter_values(obj):
  if isinstance(obj, dict):
    for v in obj.values():
      yield v
    return
  try:
    vals = obj.values()
    for v in vals:
      yield v
    return
  except Exception:
    pass
  try:
    for _, v in obj.items():
      yield v
    return
  except Exception:
    pass
  try:
    for k in obj.keys():
      yield mget(obj, k)
  except Exception:
    return

def iter_task_ids(payload):
  if hasattr(payload, 'json'):
    payload = payload.json
  if is_mapping_like(payload):
    tid = mget(payload, 'id')
    if tid:
      yield tid
    for k in ('data', 'task', 'json'):
      inner = mget(payload, k)
      if inner is not None:
        for x in iter_task_ids(inner):
          yield x
    for v in iter_values(payload):
      for x in iter_task_ids(v):
        yield x
    return
  if is_sequence_like(payload):
    try:
      n = len(payload)
    except Exception:
      n = 0
    for i in range(n):
      try:
        v = payload[i]
      except Exception:
        continue
      for x in iter_task_ids(v):
        yield x
    return
  return

def to_str(v):
  return None if v is None else str(v)

data = _getWorkflowStaticData('node')
contexts = data.get('contexts') or {}
current_key = to_str(data.get('currentPlaceholderKey'))

out_items = []

for item in _input.all():
  created_ids = list(iter_task_ids(item))
  if not created_ids:
    j = getattr(item, 'json', None)
    created_ids = list(iter_task_ids(j))
  if not created_ids:
    raise ValueError('Failed to determine created task id')

  placeholder_key = current_key
  if placeholder_key is None:
    if isinstance(contexts, dict) and len(contexts) == 1:
      placeholder_key = to_str(next(iter(contexts.keys())))
  if placeholder_key is None:
    j = getattr(item, 'json', None)
    if is_mapping_like(j):
      placeholder_key = to_str(mget(j, 'placeholderKey'))
  if placeholder_key is None:
    raise ValueError('Missing placeholder key when updating parent map')

  ctx = contexts.get(placeholder_key)
  if ctx is None:
    ctx = { 'placeholder': {}, 'parentMap': {}, 'remainingCreates': 0, 'pendingTemplateIds': [] }

  parent_map = ctx.get('parentMap') or {}
  pending = list(ctx.get('pendingTemplateIds') or [])

  for new_id in created_ids:
    if pending:
      templ_id = pending.pop(0)
    else:
      templ_id = None
      j = getattr(item, 'json', None)
      if is_mapping_like(j):
        tp = mget(j, 'task', {})
        if is_mapping_like(tp):
          templ_id = mget(tp, 'templateId')
    if templ_id is None:
      raise ValueError('Missing template id for created task (pending queue empty)')
    parent_map[templ_id] = new_id

  ctx['parentMap'] = parent_map
  ctx['pendingTemplateIds'] = pending

  rem = ctx.get('remainingCreates')
  if isinstance(rem, int) and rem > 0:
    ctx['remainingCreates'] = max(0, rem - len(created_ids))

  is_last = ctx.get('remainingCreates') in (None, 0)
  j = getattr(item, 'json', None)
  if is_mapping_like(j) and mget(j, 'isLastAction'):
    is_last = True

  if is_last:
    contexts.pop(placeholder_key, None)
    if to_str(data.get('currentPlaceholderKey')) == placeholder_key:
      data['currentPlaceholderKey'] = None
    current_key = None
  else:
    contexts[placeholder_key] = ctx

  out_items.append({ 'json': j or {} })

# persist
data['contexts'] = contexts if contexts else {}
if not contexts:
  data['currentPlaceholderKey'] = None

return out_items
