import re
from collections import defaultdict, deque

DEBUG = False

templates = _('Select Templates Project').first()
template_project_id = None
if templates:
  template_project_id = templates.json.get('templatesProjectId')

if template_project_id is None:
  if DEBUG:
    print('No template project found')
  return []

projects = [item.json for item in _('Fetch Projects').all()]
test_project_id = None
for project in projects:
  name = (project.get('name') or '').strip()
  if name == 'üß™ Test (meta)':
    test_project_id = project.get('id')
    break

if test_project_id is None:
  if DEBUG:
    print("Test project 'üß™ Test (meta)' not found")
  return []

template_tasks = [item.json for item in _('Fetch Template Tasks').all()]
if not template_tasks:
  if DEBUG:
    print('Template project has no tasks')
  return []

all_tasks = [item.json for item in _input.all()]

children = defaultdict(list)
for task in template_tasks:
  parent_id = task.get('parent_id')
  if parent_id:
    children[parent_id].append(task)

for child_list in children.values():
  child_list.sort(key=lambda item: item.get('child_order', 0))

def normalize_name(value):
  if not value:
    return ''
  lowered = value.lower()
  return ''.join(ch for ch in lowered if ch.isalnum())

template_index = [
  (task, normalize_name(task.get('content', '')))
  for task in template_tasks
]

pattern = re.compile(r'^!!\s*(.+)$', re.IGNORECASE)
items = []

for task in all_tasks:
  if task.get('project_id') == template_project_id:
    continue

  if task.get('project_id') != test_project_id:
    continue

  match = pattern.match(task.get('content', ''))
  if not match:
    continue

  template_name = match.group(1).strip()
  normalized_placeholder = normalize_name(template_name)
  if not normalized_placeholder:
    continue

  candidates = [
    t for t, normalized in template_index
    if normalized == normalized_placeholder
  ]
  candidates.sort(key=lambda t: (t.get('parent_id') is not None, t.get('child_order', 0)))
  if not candidates:
    if DEBUG:
      print(f"No template found for placeholder '{template_name}'")
    continue

  root = candidates[0]

  queue = deque([root])
  subtree = []
  while queue:
    current = queue.popleft()
    subtree.append(current)
    for child in children.get(current.get('id'), []):
      queue.append(child)

  if not subtree:
    continue

  placeholder = {
    'id': task.get('id'),
    'projectId': task.get('project_id'),
    'sectionId': task.get('section_id'),
    'parentId': task.get('parent_id'),
    'order': task.get('order'),
    'childOrder': task.get('child_order'),
  }

  placeholder_key = str(task.get('id'))

  items.append({
    'json': {
      'action': 'deletePlaceholder',
      'placeholder': placeholder,
      'templateName': template_name,
      'placeholderKey': placeholder_key,
    }
  })

  for template_task in subtree:
    template_id = template_task.get('id')
    payload = {
      'templateId': template_id,
      'parentTemplateId': template_task.get('parent_id'),
      'content': template_task.get('content', ''),
      'description': template_task.get('description'),
      'priority': template_task.get('priority'),
      'labels': template_task.get('labels') or [],
      'order': template_task.get('order'),
      'childOrder': template_task.get('child_order'),
      'due': template_task.get('due') or {},
      'isRoot': template_id == root.get('id'),
    }

    items.append({
      'json': {
        'action': 'createFromTemplate',
        'placeholder': placeholder,
        'templateName': template_name,
        'placeholderKey': placeholder_key,
        'task': payload,
      }
    })

return items

