data = this.getWorkflowStaticData('node')
placeholder = data.get('currentPlaceholder') or $json.get('placeholder')
if not placeholder:
  raise ValueError('Missing placeholder context for task creation')

data['currentPlaceholder'] = placeholder
parent_map = data.get('parentMap')
if parent_map is None:
  parent_map = {}
  data['parentMap'] = parent_map

task = $json.get('task', {})
if not task:
  raise ValueError('Missing task payload')

options = {}
section_id = placeholder.get('sectionId')

description = task.get('description')
if description:
  options['description'] = description

priority = task.get('priority')
if priority:
  options['priority'] = priority

order = placeholder.get('order') if task.get('isRoot') else None
if order is not None:
  options['order'] = order

if task.get('isRoot'):
  if section_id is not None:
    options['section'] = section_id
  parent_id = placeholder.get('parentId')
  if parent_id is not None:
    options['parentId'] = parent_id
else:
  parent_template_id = task.get('parentTemplateId')
  if parent_template_id is not None:
    parent_id = parent_map.get(parent_template_id)
    if parent_id is None:
      raise ValueError(f"Parent task for template {parent_template_id} has not been created yet")
    options['parentId'] = parent_id

due = task.get('due') or {}
if due.get('string'):
  options['dueString'] = due.get('string')
if due.get('datetime'):
  options['dueDateTime'] = due.get('datetime')
elif due.get('date'):
  options['dueDate'] = due.get('date')
if due.get('lang'):
  options['dueLang'] = due.get('lang')

labels = task.get('labels') or []

create_payload = {
  'content': task.get('content', ''),
  'project': placeholder.get('projectId'),
  'labels': labels,
  'options': options,
  'templateId': task.get('templateId'),
}

data['currentTemplateId'] = task.get('templateId')

return [{
  'json': {
    'placeholder': placeholder,
    'placeholderKey': $json.get('placeholderKey'),
    'task': task,
    'create': create_payload,
  }
}]

data = this.getWorkflowStaticData('node')
parent_map = data.get('parentMap') or {}
response = $json.get('data') or $json
new_task_id = None
if isinstance(response, dict):
  new_task_id = response.get('id') or response.get('task', {}).get('id')

if new_task_id is None:
  raise ValueError('Failed to determine created task id')

template_id = data.get('currentTemplateId')
if template_id is None:
  raise ValueError('Missing template id context for created task')

parent_map[template_id] = new_task_id
data['parentMap'] = parent_map

return _input.all()
